                    while (nAttributes-- > 0) {
                        attributes[binaryParser.getString()] = binaryParser.getString();
                    }

                    var dataType = binaryParser.getString();
                    var tileWidth = binaryParser.getFloat();

                    var nTiles = binaryParser.getInt();
